import { useState } from "react";



function Counter() {
  const [count, setCount] = us

  const increase = () => {
    count++;
    console.log(count);
  };
  const decrease = () => {
    count--;
    console.log(count);
  };


  return (
    <>
      <h2>Count: {count}</h2>

      <button onClick={increase}>Increase</button>
      <button onClick={decrease}>Decrease</button>
    </>
  )
}

export default Counter;
